function(date) {
    if (date) {
        return date.toLocaleTimeString(navigator.language, { hour: '2-digit', minute: '2-digit' });
    }

    return null;
}

function(timeStr) {
    const date = new Date();
    const dateStr = date.toLocaleString(navigator.language, { year: 'numeric', month: 'numeric', day: 'numeric' });
    return new Date(`${dateStr} ${timeStr}`);
}
